    doc_content = entities.get('content')
    output = str(doc_id) + ': '
    for keyword in keywords:
        if keyword in doc_content:
            output += '1 - '
        else:
            output += '0 - '

    court = entities.get('court')
    if court is None:
        output += 'NO COURT -'
    else:
        output += court + ' -'

    date_posted = entities.get('date_posted')
    if date_posted is None:
        output += 'NO DATE -'
    else:
        output += date_posted + ' -'

    areaoflaw = entities.get('areaoflaw')
    if areaoflaw is None:
        output += 'NO AREAOFLAW -'
    else:
        output += ','.join(areaoflaw) + ' -'
    print(output)
